CREATE OR REPLACE FUNCTION pls_obter_se_pinta_meta_equipe ( nr_seq_equipe_p bigint, dt_mes_competencia_p timestamp, ie_tipo_venda_p text) RETURNS varchar AS $body$
DECLARE


/*ie_tipo_venda_p
PJ - Pessoa Juridica
PF - Pessoa Fisica
*/
ds_retorno_w		varchar(255);
nr_seq_equipe_vend_w	bigint;
ie_tipo_contrato_w	varchar(10);
qt_meta_w		bigint;
qt_vendedores_w		bigint;


BEGIN

ds_retorno_w	:= 'N';

select	max(nr_sequencia)
into STRICT	nr_seq_equipe_vend_w
from	pls_equipe_vend_meta
where	nr_seq_equipe		= nr_seq_equipe_p
and	dt_inicio_vigencia	<= dt_mes_competencia_p
and	((ie_tipo_contrato	= ie_tipo_venda_p)
or (ie_tipo_contrato	= 'A'));

if (nr_seq_equipe_vend_w IS NOT NULL AND nr_seq_equipe_vend_w::text <> '') then
	select	ie_tipo_contrato,
		qt_meta
	into STRICT	ie_tipo_contrato_w,
		qt_meta_w
	from	pls_equipe_vend_meta
	where	nr_sequencia	= nr_seq_equipe_vend_w;

	if (ie_tipo_contrato_w	= ie_tipo_venda_p) then
		ds_retorno_w	:= 'N';
	else
		ds_retorno_w	:= 'S';
	end if;

	if (ds_retorno_w	= 'N') then
		if (qt_meta_w	= 0) then
			ds_retorno_w	:= 'S';
		end if;
	end if;
else
	ds_retorno_w	:= 'S';
end if;

if (ds_retorno_w	= 'N') then
	select	count(*)
	into STRICT	qt_vendedores_w
	from	pls_equipe_vend_vinculo
	where	nr_seq_equipe	= nr_seq_equipe_p;

	if (qt_vendedores_w	= 0) then
		ds_retorno_w	:= 'S';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
