CREATE OR REPLACE FUNCTION pls_obter_se_plano_prest_gm ( nr_seq_prestador_p pls_prestador.nr_sequencia%type, nr_seq_plano_p pls_tipo_guia_med_atend.nr_seq_plano%type, nr_seq_tipo_guia_p pls_tipo_guia_medico.nr_sequencia%type, ie_filtro_produto_p text, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1);
ie_insere_w	varchar(1) := 'S';


BEGIN

if (ie_filtro_produto_p = 'RA') then

	select	CASE WHEN count(*)=0 THEN  'N'  ELSE 'S' END
	into STRICT	ie_insere_w
	from	pls_plano_rede_atend a
	where	a.nr_seq_plano = nr_seq_plano_p;

	select	CASE WHEN count(*)=0 THEN  'N'  ELSE 'S' END
	into STRICT	ie_insere_w
	from	pls_plano_rede_atend a
	where	a.nr_seq_plano = nr_seq_plano_p
	and	pls_obter_se_prest_rede_atend(nr_seq_prestador_p,a.nr_seq_rede) = 'S'
	and	ie_insere_w = 'S';

	if	ie_insere_w = 'S' then
		if (nr_seq_tipo_guia_p IS NOT NULL AND nr_seq_tipo_guia_p::text <> '') then
			select	CASE WHEN count(*)=0 THEN  'N'  ELSE 'S' END
			into STRICT	ie_insere_w
			from	pls_tipo_guia_med_atend a
			where	a.nr_seq_plano = nr_seq_plano_p;
		end if;
	end if;
elsif (ie_filtro_produto_p = 'PP') then

	select	CASE WHEN count(*)=0 THEN  'N'  ELSE 'S' END
	into STRICT	ie_insere_w
	from	pls_prestador_plano a
	where	a.nr_seq_prestador = nr_seq_prestador_p
	and	a.nr_seq_plano 		= nr_seq_plano_p
	and	clock_timestamp() between coalesce(dt_inicio_vigencia, clock_timestamp()) and coalesce(dt_fim_vigencia, clock_timestamp())
	and	ie_situacao		= 'A'
	and	coalesce(ie_permite, 'S')	= 'S';

	if (nr_seq_tipo_guia_p IS NOT NULL AND nr_seq_tipo_guia_p::text <> '') then
		select	CASE WHEN count(*)=0 THEN  'N'  ELSE 'S' END
		into STRICT	ie_insere_w
		from	pls_tipo_guia_med_atend a
		where	a.nr_seq_plano = nr_seq_plano_p
		and	ie_insere_w = 'S';
	end if;
end if;

ds_retorno_w := ie_insere_w;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
