CREATE OR REPLACE FUNCTION pls_obter_se_porte_anestesico ( nr_seq_requisicao_p bigint, nr_seq_guia_plano_p bigint, nr_seq_conta_p bigint, nr_seq_proc_p bigint, nr_seq_mat_p bigint, ie_tipo_item_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, nr_seq_segurado_p bigint, qt_dias_p bigint, dt_emissao_p timestamp) RETURNS varchar AS $body$
DECLARE


cd_porte_anestesico_w		varchar(20);
dt_final_w			timestamp;
qt_reg_w			integer;
ds_retorno_w			varchar(1) := 'N';


BEGIN
dt_final_w := fim_dia(dt_emissao_p);
if (ie_tipo_item_p = 1) then

	select	cd_porte_anestesico
	into STRICT	cd_porte_anestesico_w
	from	pls_guia_plano_proc
	where	nr_sequencia = nr_seq_proc_p;

	if (cd_porte_anestesico_w IS NOT NULL AND cd_porte_anestesico_w::text <> '') then
		select	count(*)
		into STRICT	qt_reg_w
		from	pls_guia_plano_proc	b,
			pls_guia_plano		a
		where	b.nr_seq_guia		= a.nr_sequencia
		and	a.ie_status		= 1
		and	a.nr_seq_segurado	= nr_seq_segurado_p
		and	a.nr_sequencia 		<> nr_seq_guia_plano_p
		and	coalesce(b.cd_porte_anestesico,0) 	> 0
		and	a.dt_solicitacao between(trunc(dt_emissao_p) - qt_dias_p) and dt_final_w;

		if (qt_reg_w > 0) then
			ds_retorno_w := 'S';
		end if;
	end if;

elsif (ie_tipo_item_p = 5) then
	/*select	cd_porte_anestesico
	into	cd_porte_anestesico_w
	from	pls_requisicao_proc
	where	nr_sequencia = nr_seq_proc_p;

	if	(cd_porte_anestesico_w is not null) then*/
	select	count(*)
	into STRICT	qt_reg_w
	from	pls_requisicao_proc	b,
		pls_requisicao		a
	where	b.nr_seq_requisicao	= a.nr_sequencia
	and	a.ie_estagio		= 2
	and	b.ie_status		in ('P','S')
	and	a.nr_seq_segurado	= nr_seq_segurado_p
	and	a.nr_sequencia 		<> nr_seq_requisicao_p
	and	coalesce(b.cd_porte_anestesico,0) 	> 0
	and	a.dt_requisicao between(trunc(dt_emissao_p) - qt_dias_p) and dt_final_w;

	if (qt_reg_w > 0) then
		ds_retorno_w := 'S';
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
