CREATE OR REPLACE FUNCTION pls_obter_se_possui_hist_event ( nr_seq_evento_atend_p bigint) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Verificar se o evento do atendimento possui algum histórico padrão cadastrado na função
Gestão de Atendimento (call center) / Cadastros / Ocorrências / Eventos
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:Performance.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w			varchar(1)	:= 'N';
qt_historico_evt_w		bigint;


BEGIN

if (nr_seq_evento_atend_p IS NOT NULL AND nr_seq_evento_atend_p::text <> '') then
	select	count(1)
	into STRICT	qt_historico_evt_w
	from	pls_evento_ocor_historico a,
		pls_evento_ocorrencia b
	where	a.nr_seq_evento_ocor	= b.nr_sequencia
	and	b.nr_sequencia		= nr_seq_evento_atend_p;

	if (qt_historico_evt_w	> 0) then
		ds_retorno_w	:= 'S';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
