CREATE OR REPLACE FUNCTION pls_obter_se_prestador_duplic ( cd_pessoa_fisica_p text, cd_cgc_p text, nr_seq_prestador_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1);

/*---------------------------------------------------------*/

/*     S    -    Possui Prestador cadastrado           */

/*     N    -   NÃ£o possui Prestador cadastrado    */

/*---------------------------------------------------------*/

BEGIN
ds_retorno_w	:= 'N';

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and (nr_seq_prestador_p IS NOT NULL AND nr_seq_prestador_p::text <> '') then

	select	CASE WHEN coalesce(max(nr_sequencia),0)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from	pls_prestador
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p
	and	nr_sequencia		<> nr_seq_prestador_p;

elsif (cd_cgc_p IS NOT NULL AND cd_cgc_p::text <> '') and (nr_seq_prestador_p IS NOT NULL AND nr_seq_prestador_p::text <> '') then

	select	CASE WHEN coalesce(max(nr_sequencia),0)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from	pls_prestador
	where	cd_cgc		= cd_cgc_p
	and	nr_sequencia	<> nr_seq_prestador_p;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
