CREATE OR REPLACE FUNCTION pls_obter_se_prestador_grupo ( nr_seq_grupo_prest_p bigint, nr_seq_prestador_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w			varchar(2)	:= 'N';
nr_seq_classificacao_w		bigint;
qt_registros_w			bigint;


BEGIN

if (nr_seq_prestador_p IS NOT NULL AND nr_seq_prestador_p::text <> '') and (nr_seq_grupo_prest_p IS NOT NULL AND nr_seq_grupo_prest_p::text <> '') then
	select	coalesce(nr_seq_classificacao,0)
	into STRICT	nr_seq_classificacao_w
	from	pls_prestador
	where	nr_sequencia	= nr_seq_prestador_p;

	select	count(*)
	into STRICT	qt_registros_w
	from	pls_preco_prestador a,
		pls_preco_grupo_prestador b
	where	b.nr_sequencia		= a.nr_seq_grupo
	and	a.nr_seq_grupo		= nr_seq_grupo_prest_p
	and (coalesce(a.nr_seq_prestador::text, '') = '' or a.nr_seq_prestador		= nr_seq_prestador_p)
	and (coalesce(a.nr_seq_classificacao::text, '') = ''	or a.nr_seq_classificacao	= nr_seq_classificacao_w)
	and	b.ie_situacao		= 'A';

	if (qt_registros_w	> 0) then
		ie_retorno_w	:= 'S';
	end if;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
