CREATE OR REPLACE FUNCTION pls_obter_se_prestador_pacote ( nr_seq_prestador_p bigint, nr_seq_conta_proc_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w 		varchar(1);
nr_seq_regra_w		bigint;
nr_seq_preco_pacote_w	bigint;


BEGIN

ds_retorno_w := 'N';
select 	max(nr_seq_preco_pacote)
into STRICT	nr_seq_preco_pacote_w
from 	pls_conta_proc
where	nr_sequencia = nr_seq_conta_proc_p;

/*askono - alterei para que seja  comparado o prestador do pacote e nao o prestador da regra de pacote , pois este prestador da regra de pacote Ã© cadastrado como regra */

if (coalesce(nr_seq_preco_pacote_w,0) > 0) then
	select	max(a.nr_sequencia)
	into STRICT	nr_seq_regra_w
	from	pls_pacote_tipo_acomodacao 	a,
		pls_pacote 			b
	where	b.nr_sequencia 	=  a.nr_Seq_pacote
	and	a.nr_Sequencia 	=  nr_seq_preco_pacote_w
	and	((coalesce(b.nr_seq_prestador::text, '') = '') or (b.nr_seq_prestador = nr_seq_prestador_p));

	if (coalesce(nr_seq_regra_w,0) > 0 ) then
		ds_retorno_w := 'S';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
