CREATE OR REPLACE FUNCTION pls_obter_se_prest_a400 ( nr_seq_conta_p pls_conta.nr_sequencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) RETURNS varchar AS $body$
DECLARE


qt_prestador_a400_w	integer;
ie_consiste_prest_w	pls_parametros.ie_consiste_prest_envio_a500%type;
ie_envia_w		varchar(1);


BEGIN

ie_envia_w := 'S';

if (nr_seq_conta_p IS NOT NULL AND nr_seq_conta_p::text <> '') and (cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '') then

	-- Prestador A400
	select	coalesce(max(ie_consiste_prest_envio_a500),'N')
	into STRICT	ie_consiste_prest_w
	from	pls_parametros
	where	cd_estabelecimento = cd_estabelecimento_p;

	if (nr_seq_conta_p IS NOT NULL AND nr_seq_conta_p::text <> '') and (ie_consiste_prest_w = 'S') then

		select	count(1)
		into STRICT	qt_prestador_a400_w
		from	ptu_movimento_prestador c,
			ptu_prestador b,
			pls_conta a
		where	a.nr_sequencia = nr_seq_conta_p
		and	b.nr_seq_prestador = a.nr_seq_prestador_exec
		and	c.nr_sequencia = b.nr_seq_movimento
		and	(c.dt_liberacao IS NOT NULL AND c.dt_liberacao::text <> '');

		if (qt_prestador_a400_w = 0) then

			ie_envia_w := 'N'; --Motivo: Prestador n√£o enviado no A400
		end if;
	end if;
end if;

return ie_envia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
