CREATE OR REPLACE FUNCTION pls_obter_se_prest_mat_web ( nr_seq_prestador_p bigint, nr_seq_usuario_web_p bigint) RETURNS varchar AS $body$
DECLARE



qt_registros_w		bigint;
ds_retorno_w		varchar(1) := 'N';


BEGIN

select	count(1)
into STRICT	qt_registros_w
from	pls_prestador a,
	pls_prestador_usuario_web b
where 	a.nr_sequencia = b.nr_seq_prestador
and	b.nr_seq_usuario = nr_seq_usuario_web_p
and	a.nr_sequencia = nr_seq_prestador_p
and	a.ie_situacao = 'A'
and	b.ie_situacao = 'A';

if (qt_registros_w > 0) then
	ds_retorno_w := 'S';

elsif (pls_obter_se_controle_estab('RE') = 'S')then
	select	count(1)
	into STRICT	qt_registros_w
	from	pls_prestador a,
		pls_lib_prest_solic_mat b
	where	a.nr_sequencia  = b.nr_seq_prestador
	and	b.nr_seq_usuario_web = nr_seq_usuario_web_p
	and	a.nr_sequencia = nr_seq_prestador_p
	and	a.ie_situacao = 'A'
	and	b.ie_situacao = 'A'
	and	trunc(b.dt_inicio_vigencia) <= clock_timestamp()
	and	coalesce(trunc(b.dt_fim_vigencia), clock_timestamp()) >= clock_timestamp()
	and 	coalesce(b.cd_estabelecimento, wheb_usuario_pck.get_cd_estabelecimento) = wheb_usuario_pck.get_cd_estabelecimento;

	if (qt_registros_w > 0) then
		ds_retorno_w := 'S';
	end if;
else
	select	count(1)
	into STRICT	qt_registros_w
	from	pls_prestador a,
		pls_lib_prest_solic_mat b
	where	a.nr_sequencia  = b.nr_seq_prestador
	and	b.nr_seq_usuario_web = nr_seq_usuario_web_p
	and	a.nr_sequencia = nr_seq_prestador_p
	and	a.ie_situacao = 'A'
	and	b.ie_situacao = 'A'
	and	trunc(b.dt_inicio_vigencia) <= clock_timestamp()
	and	coalesce(trunc(b.dt_fim_vigencia), clock_timestamp()) >= clock_timestamp();

	if (qt_registros_w > 0) then
		ds_retorno_w := 'S';
	end if;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
