CREATE OR REPLACE FUNCTION pls_obter_se_prest_plano ( nr_seq_prest_p pls_prestador.nr_sequencia%type, nr_seq_plano_p pls_plano.nr_sequencia%type, dt_referencia_p timestamp) RETURNS varchar AS $body$
DECLARE


ie_insere_w		varchar(1)	:= 'N';
qt_prest_w		integer;


BEGIN

select	count(1)
into STRICT	qt_prest_w
from	pls_prestador_plano
where	nr_seq_prestador	= nr_seq_prest_p
and	nr_seq_plano		= nr_seq_plano_p
and	dt_referencia_p between coalesce(dt_inicio_vigencia, dt_referencia_p) and coalesce(dt_fim_vigencia, dt_referencia_p)
and	ie_situacao		= 'A'
and	coalesce(ie_permite, 'S')	= 'S';

if (qt_prest_w > 0)then
	ie_insere_w	:= 'S';
end if;

return	ie_insere_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
