CREATE OR REPLACE FUNCTION pls_obter_se_prest_rede_atend ( nr_seq_prestador_p bigint, nr_seq_rede_p bigint) RETURNS varchar AS $body$
DECLARE


ie_permite_w			varchar(2)	:= 'N';
nr_seq_classificacao_w		bigint;
nr_seq_tipo_prestador_w		bigint;
cd_prestador_w			varchar(30);
ie_tipo_rede_min_ptu_w		pls_rede_atend_regra.ie_tpo_rede_ptu_prest%type;
ie_prestador_alto_custo_w	pls_prestador.ie_prestador_alto_custo%type;

C01 CURSOR FOR
	SELECT	ie_permite
	from	pls_prestador_rede_atend
	where	((nr_seq_prestador = coalesce(nr_seq_prestador_p,nr_seq_prestador)) or (coalesce(nr_seq_prestador::text, '') = ''))
	and	((nr_seq_classif_prestador = coalesce(nr_seq_classificacao_w,nr_seq_classif_prestador)) or (coalesce(nr_seq_classif_prestador::text, '') = ''))
	and	((nr_seq_tipo_prestador = coalesce(nr_seq_tipo_prestador_w,nr_seq_tipo_prestador)) or (coalesce(nr_seq_tipo_prestador::text, '') = ''))
	and	nr_seq_rede_atend	= nr_seq_rede_p
	and	clock_timestamp() between coalesce(dt_inicio_vigencia,clock_timestamp()) and coalesce(dt_fim_vigencia,clock_timestamp())
	order by
		coalesce(nr_seq_prestador,0),
		coalesce(nr_seq_classif_prestador,0),
		coalesce(nr_seq_tipo_prestador,0);

C02 CURSOR FOR
	SELECT	ie_permite
	from	pls_rede_atend_regra
	where	((nr_seq_prestador = coalesce(nr_seq_prestador_p,nr_seq_prestador)) or (coalesce(nr_seq_prestador::text, '') = ''))
	and	((nr_seq_classif_prestador = coalesce(nr_seq_classificacao_w,nr_seq_classif_prestador)) or (coalesce(nr_seq_classif_prestador::text, '') = ''))
	and	((nr_seq_tipo_prestador = coalesce(nr_seq_tipo_prestador_w,nr_seq_tipo_prestador)) or (coalesce(nr_seq_tipo_prestador::text, '') = ''))
	and	((coalesce(cd_prestador::text, '') = '') or (cd_prestador = coalesce(cd_prestador_w,'0')))
	and	((coalesce(ie_alto_custo_prest::text, '') = '') or (ie_alto_custo_prest	in ('A', coalesce(ie_prestador_alto_custo_w, 'N'))))
	and	((coalesce(ie_tpo_rede_ptu_prest::text, '') = '') or (ie_tpo_rede_ptu_prest in ('N', ie_tipo_rede_min_ptu_w)))
	and	nr_seq_rede	= nr_seq_rede_p
	order by
		coalesce(cd_prestador,0),
		coalesce(nr_seq_prestador,0),
		coalesce(nr_seq_classif_prestador,0),
		coalesce(nr_seq_tipo_prestador,0),
		coalesce(ie_congenere_inferior,'X'),
		coalesce(cd_procedimento,0) desc,
		coalesce(nr_seq_congenere,0) desc;


BEGIN
select	max(nr_seq_classificacao),
	max(nr_seq_tipo_prestador),
	max(cd_prestador),
	CASE WHEN max(ie_tipo_rede_min_ptu)=1 THEN  'B' WHEN max(ie_tipo_rede_min_ptu)=2 THEN  'E' WHEN max(ie_tipo_rede_min_ptu)=3 THEN  'M'  ELSE 'N' END ,
	max(ie_prestador_alto_custo)
into STRICT	nr_seq_classificacao_w,
	nr_seq_tipo_prestador_w,
	cd_prestador_w,
	ie_tipo_rede_min_ptu_w,
	ie_prestador_alto_custo_w
from	pls_prestador
where	nr_sequencia	= nr_seq_prestador_p;

open C01;
loop
fetch C01 into
	ie_permite_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	null;
	end;
end loop;
close C01;

if (ie_permite_w = 'N') then
	open C02;
	loop
	fetch C02 into
		ie_permite_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin
		null;
		end;
	end loop;
	close C02;
end if;


return	ie_permite_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
