CREATE OR REPLACE FUNCTION pls_obter_se_proc_atend ( cd_procedimento_p bigint, ie_origem_proced_p bigint, nr_seq_proc_tipo_atend_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);
cd_area_proc_w		numeric(30);
cd_grupo_proc_w		numeric(30);
cd_especialidade_w	numeric(30);
ie_origem_proced_w	numeric(30);


BEGIN

-- jjung OS 491904 07/09/2012 : obter a estrutura do procedimento
SELECT * FROM pls_obter_estrut_proc(	cd_procedimento_p, ie_origem_proced_p, cd_area_proc_w, cd_especialidade_w, cd_grupo_proc_w, ie_origem_proced_w) INTO STRICT cd_area_proc_w, cd_especialidade_w, cd_grupo_proc_w, ie_origem_proced_w;

select	CASE WHEN count(1)=0 THEN 'N'  ELSE 'S' END
into STRICT	ds_retorno_w
from	pls_oc_proc_atend_regra
where	cd_procedimento		= cd_procedimento_p
and	ie_origem_proced	= ie_origem_proced_p
and	nr_seq_regra		= nr_seq_proc_tipo_atend_p;

/* jjung OS 491904 07/09/2012: Se não existir regra para o procedimento, verifica o grupo,
Se não existir regra para o grupo, verifica a especialidade, Se não existir regra para a especialidade, verifica para a area */
if (coalesce(ds_retorno_w,'N') = 'N') then
	select	CASE WHEN coalesce(count(1),0)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from	pls_oc_proc_atend_regra
	where	cd_grupo_proc		= cd_grupo_proc_w
	and	nr_seq_regra		= nr_seq_proc_tipo_atend_p;
end if;

if (coalesce(ds_retorno_w,'N') = 'N') then
	select	CASE WHEN coalesce(count(1),0)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from	pls_oc_proc_atend_regra
	where	cd_especialidade	= cd_especialidade_w
	and	nr_seq_regra		= nr_seq_proc_tipo_atend_p;
end if;

if (coalesce(ds_retorno_w,'N') = 'N') then
	select	CASE WHEN coalesce(count(1),0)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from	pls_oc_proc_atend_regra
	where	cd_area_procedimento	= cd_area_proc_w
	and	nr_seq_regra		= nr_seq_proc_tipo_atend_p;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
