CREATE OR REPLACE FUNCTION pls_obter_se_proc_ativo ( cd_procedimento_p text, ie_origem_proced_p bigint, dt_procedimento_p timestamp) RETURNS varchar AS $body$
DECLARE


/*
ds_retorno_w:

S - procedimento ativo
N - procedimento inativo
D- procedimento fora da data da regra de vigÃªncia
*/
ds_retorno_w	varchar(1)	:= 'N';
qt_registros_w	numeric(30)	:= 0;


BEGIN

select	count(1)
into STRICT	qt_registros_w
from	procedimento
where	cd_procedimento		= cd_procedimento_p
and	ie_origem_proced	= ie_origem_proced_p
and	ie_situacao 		= 'A';

if (coalesce(qt_registros_w,0) > 0) then
	ds_retorno_w	:= pls_consiste_vigencia_proc(cd_procedimento_p,ie_origem_proced_p,dt_procedimento_p);
	if (ds_retorno_w = 'N') then
		ds_retorno_w := 'D';
	end if;
else
	ds_retorno_w := 'N';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
