SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_se_proc_auditor ( nr_seq_proc_p bigint, ie_tipo_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


/* IE_TIPO_P
	1 - Autorização
	2 - Contas médicas
*/
ie_retorno_w		varchar(1)	:= 'S';
cd_pessoa_fisica_w	varchar(10);
ie_status_w		varchar(2);
nr_seq_regra_lib_w	bigint;
qt_registros_w		integer	:= 0;


BEGIN

if (ie_tipo_p	= 1) then
	begin
	select	coalesce(ie_status, 'X'),
		coalesce(nr_seq_regra_liberacao,0)
	into STRICT	ie_status_w,
		nr_seq_regra_lib_w
	from	pls_guia_plano_proc
	where	nr_sequencia	= nr_seq_proc_p;
	exception
		when others then
		ie_status_w		:= 'X';
		nr_seq_regra_lib_w	:= 0;
	end;
elsif (ie_tipo_p	= 2) then
	begin
	select	coalesce(ie_status, 'X'),
		coalesce(nr_seq_regra_liberacao,0)
	into STRICT	ie_status_w,
		nr_seq_regra_lib_w
	from	pls_conta_proc
	where	nr_sequencia	= nr_seq_proc_p;
	exception
		when others then
		ie_status_w		:= 'X';
		nr_seq_regra_lib_w	:= 0;
	end;
end if;

if (ie_status_w		= 'A') and (nr_seq_regra_lib_w	> 0) then
	/* Obter a pessoa física do usuário */

	/*select 	nvl(max(cd_pessoa_fisica),'')
	into	cd_pessoa_fisica_w
	from	usuario
	where	nm_usuario	= nm_usuario_p;*/
	select	count(*)
	into STRICT	qt_registros_w
	from	pls_membro_grupo_aud	d,
		pls_grupo_auditor	c,
		pls_lib_grupo_auditor	b,
		pls_regra_liberacao	a
	where	a.nr_sequencia		= nr_seq_regra_lib_w
	and	d.nm_usuario_exec	= nm_usuario_p
	and	a.nr_sequencia		= b.nr_seq_regra_lib
	and	b.nr_seq_grupo_auditor	= c.nr_sequencia
	and	c.nr_sequencia		= d.nr_seq_grupo
	and	c.ie_situacao		= 'A'
	and	d.ie_situacao		= 'A';

	if (qt_registros_w	= 0) then
		ie_retorno_w	:= 'N';
	end if;

end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
