CREATE OR REPLACE FUNCTION pls_obter_se_proc_autorizado ( cd_procedimento_p bigint, ie_origem_proced_p bigint, nr_seq_guia_p bigint, nr_seq_segurado_p bigint, dt_solicitacao_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(1) := 'S';
qt_reg_w			integer;


BEGIN

select	count(*)
into STRICT	qt_reg_w
from	pls_guia_plano	a
where	a.nr_sequencia		<> nr_seq_guia_p
and	a.nr_seq_segurado	= nr_seq_segurado_p
and	a.ie_status		= '1'
and	dt_solicitacao_p	< a.dt_validade_senha
and	exists (	SELECT	1
			from	pls_guia_plano_proc x
			where	x.nr_seq_guia		= a.nr_sequencia
			and	x.cd_procedimento	= cd_procedimento_p
			and	x.ie_origem_proced	= ie_origem_proced_p);

if (qt_reg_w > 0) then
	ds_retorno_w := 'N';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
