SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_se_proc_cpt_pac ( cd_procedimento_p bigint, ie_origem_proced_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w			varchar(2);
ds_retorno_w			varchar(120);
nr_seq_rol_w			bigint;


BEGIN

select	coalesce(max(nr_sequencia),0)
into STRICT	nr_seq_rol_w
from	pls_rol
where	dt_inicio_vigencia	= (SELECT max(dt_inicio_vigencia) from pls_rol);

select	coalesce(max(ie_complexidade),'X')
into STRICT	ie_retorno_w
from	pls_rol_capitulo	e,
	pls_rol_grupo		d,
	pls_rol_subgrupo	c,
	pls_rol_grupo_proc	b,
	pls_rol_procedimento	a
where	e.nr_seq_rol		= nr_seq_rol_w
and	a.nr_seq_rol_grupo	= b.nr_sequencia
and	a.cd_procedimento	= cd_procedimento_p
and	a.ie_origem_proced	= ie_origem_proced_p
and	b.nr_seq_subgrupo	= c.nr_sequencia
and	c.nr_seq_grupo		= d.nr_sequencia
and	d.nr_seq_capitulo	= e.nr_sequencia
and	coalesce(b.ie_situacao,'A')	= 'A'
and	coalesce(c.ie_situacao,'A')	= 'A'
and	coalesce(d.ie_situacao,'A')	= 'A'
and	coalesce(e.ie_situacao,'A')	= 'A';

if (ie_retorno_w	= 'X') and (cd_procedimento_p IS NOT NULL AND cd_procedimento_p::text <> '') then
	ie_retorno_w		:= 'NR';
elsif (ie_retorno_w	= 'X') then
	ie_retorno_w		:= '';
end if;

if (ie_opcao_p	= 'C') then
	ds_retorno_w	:= ie_retorno_w;
elsif (ie_retorno_w	= 'S') then
	ds_retorno_w	:= 'Sim';
elsif (ie_retorno_w	= 'N') then
	ds_retorno_w	:= 'Não';
elsif (ie_retorno_w	= 'NR') then
	ds_retorno_w	:= 'Não consta no Rol';
else
	ds_retorno_w	:= '';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
