CREATE OR REPLACE FUNCTION pls_obter_se_proc_inativo ( cd_procedimento_p bigint, ie_origem_proced_p bigint, dt_procedimento_p timestamp) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Verificar se o procedimento está inativo
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção: Performance.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w			varchar(1)	:= 'S';
ie_vigencia_proc_w		varchar(1)	:= 'S';
ie_situacao_proc_w		varchar(1)	:= 'A';


BEGIN

begin
	ie_vigencia_proc_w :=	pls_consiste_vigencia_proc(cd_procedimento_p,ie_origem_proced_p,dt_procedimento_p);

	select	ie_situacao
	into STRICT	ie_situacao_proc_w
	from	procedimento
	where	cd_procedimento	= cd_procedimento_p
	and	ie_origem_proced = ie_origem_proced_p;
exception
when others then
	ie_vigencia_proc_w := 'S';
	ie_situacao_proc_w := 'A';
end;

if (ie_vigencia_proc_w = 'N') or (ie_situacao_proc_w = 'I') then
	ds_retorno_w := 'N';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
