CREATE OR REPLACE FUNCTION pls_obter_se_proc_mat_agd_env ( nr_seq_requisicao_p bigint, nr_seq_guia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1) := 'N';
qt_proc_w		bigint;
qt_proc_interc_w	bigint;
qt_mat_w		bigint;
qt_mat_interc_w		bigint;


BEGIN

if (coalesce(nr_seq_requisicao_p,0) <> 0) then
	select 	count(1)
	into STRICT	qt_proc_w
	from 	pls_requisicao_proc b
	where 	b.nr_seq_requisicao = nr_seq_requisicao_p;

	select 	count(1)
	into STRICT	qt_proc_interc_w
	from 	pls_requisicao_proc b
	where 	b.nr_seq_requisicao = nr_seq_requisicao_p
	and 	b.ie_status = 'I';

	select 	count(1)
	into STRICT	qt_mat_w
	from 	pls_requisicao_mat b
	where 	b.nr_seq_requisicao = nr_seq_requisicao_p;

	select 	count(1)
	into STRICT	qt_mat_interc_w
	from 	pls_requisicao_mat b
	where 	b.nr_seq_requisicao = nr_seq_requisicao_p
	and 	b.ie_status = 'I';

	if (qt_proc_w	= qt_proc_interc_w)	and (qt_mat_w	= qt_mat_interc_w) then
		ds_retorno_w	:= 'S';
	else
		ds_retorno_w	:= 'N';
	end if;

elsif (coalesce(nr_seq_guia_p,0) <> 0) then
	select 	count(1)
	into STRICT	qt_proc_w
	from 	pls_guia_plano_proc b
	where 	b.nr_seq_guia = nr_seq_guia_p;

	select 	count(1)
	into STRICT	qt_proc_interc_w
	from 	pls_guia_plano_proc b
	where 	b.nr_seq_guia = nr_seq_guia_p
	and 	b.ie_status = 'I';

	select 	count(1)
	into STRICT	qt_mat_w
	from 	pls_guia_plano_mat b
	where 	b.nr_seq_guia = nr_seq_guia_p;

	select 	count(1)
	into STRICT	qt_mat_interc_w
	from 	pls_guia_plano_mat b
	where 	b.nr_seq_guia = nr_seq_guia_p
	and 	b.ie_status = 'I';

	if (qt_proc_w	= qt_proc_interc_w)	and (qt_mat_w	= qt_mat_interc_w) then
		ds_retorno_w	:= 'S';
	else
		ds_retorno_w	:= 'N';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
