CREATE OR REPLACE FUNCTION pls_obter_se_proc_pend_interc ( nr_seq_guia_p bigint) RETURNS varchar AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Verificar nos casos de guias de intercâmbio, se a operadora é OFFline, se existem itens que não se encaixaram nas regras de liberação.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[]  Objetos do dicionário [X] Tasy (Delphi/Java) [] Portal []  Relatórios [] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:Performance.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
qt_reg_aguard_w			bigint;
ie_estagio_guia_w		bigint;
ie_retorno_w			varchar(1);


BEGIN

if (pls_obter_se_scs_ativo	= 'I') then
	select	count(1)
	into STRICT	qt_reg_aguard_w
	from	pls_guia_plano_proc
	where	nr_seq_guia	= nr_seq_guia_p
	and	ie_status	= 'I';

	select	ie_estagio
	into STRICT	ie_estagio_guia_w
	from	pls_guia_plano
	where	nr_sequencia	= nr_seq_guia_p;

	if (qt_reg_aguard_w	= 0) and (ie_estagio_guia_w not in (1,2)) then
		ie_retorno_w	:= 'N';
	end if;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_se_proc_pend_interc ( nr_seq_guia_p bigint) FROM PUBLIC;

