CREATE OR REPLACE FUNCTION pls_obter_se_proc_reincide ( nr_seq_conta_proc_p bigint, nr_seq_segurado_p bigint, qt_dias_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, dt_procedimento_p timestamp, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_reincidente_w	varchar(1) := 'N';

C01 CURSOR FOR
	SELECT	'S'
	from	pls_conta	a
	where	a.nr_seq_segurado	= nr_seq_segurado_p
	and	a.cd_estabelecimento 	= cd_estabelecimento_p
	and	exists (SELECT	x.nr_sequencia
			from	pls_conta_proc x,
				pls_conta y
			where	x.nr_seq_conta = a.nr_sequencia
			and	x.nr_seq_conta = y.nr_sequencia
			and	x.cd_procedimento = cd_procedimento_p
			and	x.ie_origem_proced = ie_origem_proced_p
			and	coalesce(x.dt_procedimento, y.dt_emissao) between(dt_procedimento_p - qt_dias_p) and dt_procedimento_p
			and	x.nr_sequencia <> nr_seq_conta_proc_p);


BEGIN

open C01;
loop
fetch C01 into
	ie_reincidente_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
end loop;
close C01;

return	ie_reincidente_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
