CREATE OR REPLACE FUNCTION pls_obter_se_produto_inativo ( nr_seq_conta_p bigint, nr_seq_segurado_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w			varchar(1);
ie_situacao_w			varchar(1);


BEGIN

if (nr_seq_conta_p IS NOT NULL AND nr_seq_conta_p::text <> '') then
	/*exception se não existir beneficiário deverá retornar uma outra glosa beneficiário não existente...*/

	begin
	select	b.ie_situacao
	into STRICT	ie_situacao_w
	from	pls_plano	b,
		pls_segurado	a
	where	a.nr_seq_plano = b.nr_sequencia
	and	a.nr_sequencia = nr_seq_segurado_p;
	exception
	when others then
		ie_situacao_w := 'A';
	end;

	if (ie_situacao_w = 'A') then
		ie_retorno_w := 'N';
	else
		ie_retorno_w := 'S';
	end if;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
