CREATE OR REPLACE FUNCTION pls_obter_se_prot_apresentacao (nr_seq_protocolo_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(2);
ie_apresentacao_w	varchar(3);


BEGIN
select	coalesce(ie_apresentacao,'A')
into STRICT	ie_apresentacao_w
from	pls_protocolo_conta
where	nr_sequencia = nr_seq_protocolo_p;

if (ie_apresentacao_w = 'R') then
	ds_retorno_w := 'N';
else
	ds_retorno_w := 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
