CREATE OR REPLACE FUNCTION pls_obter_se_qtde_aux_excedeu (ie_tipo_guia_p text, nr_seq_conta_p bigint, nr_seq_conta_proc_p bigint, nr_auxiliares_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: 	deverá verificar se dentre os auxiliares da conta/item não existe
		nenhum que exeda o número de auxiliares permitidos
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  X]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
vl_posicao_auxiliar_w		bigint;
nr_seq_grau_partic_w		bigint;
ie_auxiliar_w			varchar(1);
ie_auxiliar_maior_w		varchar(1)	:= 'N';


C01 CURSOR FOR
	SELECT	c.ie_auxiliar,
		coalesce(c.vl_posicao_auxiliar,0)
	from	pls_grau_participacao	c,
		pls_conta		a
	where	a.nr_sequencia		= nr_seq_conta_p
	and	a.nr_seq_grau_partic	= c.nr_sequencia;

C02 CURSOR FOR
	SELECT	c.ie_auxiliar,
		coalesce(c.vl_posicao_auxiliar,0)
	from	pls_proc_participante	b,
		pls_grau_participacao	c,
		pls_conta_proc		a
	where	a.nr_sequencia		= b.nr_seq_conta_proc
	and	b.nr_seq_grau_partic	= c.nr_sequencia
	and	a.nr_sequencia		= nr_seq_conta_proc_p
	and	coalesce(b.ie_status,'X')		<> 'C';

BEGIN

open C01;
loop
fetch C01 into
	ie_auxiliar_w,
	vl_posicao_auxiliar_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (ie_auxiliar_w = 'S') and (vl_posicao_auxiliar_w > nr_auxiliares_p ) then
		ie_auxiliar_maior_w := 'S';
		goto final;
	end if;
	end;
end loop;
close C01;

open C02;
loop
fetch C02 into
	ie_auxiliar_w,
	vl_posicao_auxiliar_w;
EXIT WHEN NOT FOUND; /* apply on C02 */
	begin
	if (ie_auxiliar_w = 'S') and (vl_posicao_auxiliar_w > nr_auxiliares_p ) then
		ie_auxiliar_maior_w := 'S';
		goto final;
	end if;
	end;
end loop;
close C02;

<<final>>
return 	ie_auxiliar_maior_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
