SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_se_qt_dias_cont ( nr_seq_protocolo_p bigint ) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(1);
dt_geracao_registro_w		timestamp;
cd_unimed_origem_w		bigint;
qt_dias_contestacao_w		bigint;
nr_seq_unimed_origem_w		bigint;
cd_estabelecimento_w		smallint;

C01 CURSOR FOR
	SELECT	qt_dias_contestacao
	from	pls_regra_intercambio	a
	where	trunc(dt_geracao_registro_w) between trunc(dt_inicio_vigencia) and fim_dia(coalesce(dt_fim_vigencia,clock_timestamp()))
	and	coalesce(nr_seq_congenere_sup,coalesce((nr_seq_congenere)::numeric ,nr_seq_unimed_origem_w)) = (nr_seq_unimed_origem_w)::numeric
	and	((pls_obter_tipo_intercambio(nr_seq_unimed_origem_w,cd_estabelecimento_w) = coalesce(ie_tipo_intercambio,'A')) or (coalesce(ie_tipo_intercambio,'A') = 'A'))
	and	(((a.nr_seq_grupo_congenere IS NOT NULL AND a.nr_seq_grupo_congenere::text <> '') and exists ( 	SELECT	1
									from	pls_cooperativa_grupo	x
									where	x.nr_seq_grupo = a.nr_seq_grupo_congenere
									and	x.nr_seq_congenere = nr_seq_unimed_origem_w )) or
		coalesce(a.nr_seq_grupo_congenere::text, '') = '')
	order by 1;


BEGIN
/*Obter recebimento do A500 e sua origem*/

begin
select	cd_unimed_origem,
	dt_recebimento_fatura,
	cd_estabelecimento
into STRICT	cd_unimed_origem_w,
	dt_geracao_registro_w,
	cd_estabelecimento_w
from	ptu_fatura
where	nr_seq_protocolo = nr_seq_protocolo_p;
exception
when others then
	cd_unimed_origem_w	:= null;
end;

/*Obter o seq da unimed origem*/

begin
select	nr_sequencia
into STRICT	nr_seq_unimed_origem_w
from	pls_congenere
where	(cd_cooperativa)::numeric  = cd_unimed_origem_w;
exception
when others then
	nr_seq_unimed_origem_w	:= null;
end;

/*Obter regra de envio*/

open C01;
loop
fetch C01 into
	qt_dias_contestacao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
end loop;
close C01;

if (coalesce(qt_dias_contestacao_w,0) >= 0) then
	if	(clock_timestamp() > (dt_geracao_registro_w + qt_dias_contestacao_w)) then
		ds_retorno_w := 'N';
	else
		ds_retorno_w := 'S';
	end if;
else
	ds_retorno_w := 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
