CREATE OR REPLACE FUNCTION pls_obter_se_ramal_pausa ( nr_ramal_p text, nr_seq_operador_cont_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(1);
nr_seq_operador_controle_w	bigint;
dt_saida_w			timestamp;


BEGIN

if (nr_seq_operador_cont_p > 0) then
	begin
		select	dt_saida
		into STRICT	dt_saida_w
		from	pls_operador_controle
		where	nr_sequencia = nr_seq_operador_cont_p;
	exception
	when others then
		dt_saida_w := null;
	end;

	if (coalesce(dt_saida_w::text, '') = '') then
		ds_retorno_w := 'L';
	else
		begin
			select	coalesce(ie_pausa_central,'N')
			into STRICT	ds_retorno_w
			from	pls_operador_controle
			where	nr_sequencia = nr_seq_operador_cont_p;
		exception
		when others then
			ds_retorno_w := 'N';
		end;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
