CREATE OR REPLACE FUNCTION pls_obter_se_reajuste_aplic (nr_seq_contrato_p bigint, dt_referencia_p timestamp) RETURNS varchar AS $body$
DECLARE


ie_reajuste_w		varchar(1)	:= 'S';
dt_reajuste_w		timestamp;
dt_contrato_w		timestamp;
qt_intervalo_w		bigint;
qt_registros_w		bigint;


BEGIN

ie_reajuste_w		:= 'N';

select	add_months(dt_contrato,coalesce(qt_intervalo,12)),
	dt_contrato
into STRICT	dt_reajuste_w,
	dt_contrato_w
from	pls_contrato
where	nr_sequencia	= nr_seq_contrato_p;

if (trunc(dt_contrato_w,'month') < trunc(clock_timestamp(),'month')) and (to_char(dt_reajuste_w,'mm') = to_char(dt_referencia_p,'mm')) then

	ie_reajuste_w		:= 'S';

	select	count(*)
	into STRICT	qt_registros_w
	from	pls_lote_reaj_segurado
	where	nr_seq_contrato	= nr_seq_contrato_p
	and	ie_status	= '2'
	and	to_char(dt_mesano_referencia,'mm/yyyy') = to_char(dt_referencia_p,'mm/yyyy');

	if (qt_registros_w > 0) then
		ie_reajuste_w	:= 'N';
	end if;

end if;

return ie_reajuste_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
