SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_se_reajuste_retific (nr_seq_reajuste_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w 		varchar(1);
qt_reajuste_mes_w	integer;


BEGIN

select	count(1)
into STRICT	qt_reajuste_mes_w
from	pls_reajuste	a
where	a.nr_sequencia = nr_seq_reajuste_p
and	exists (	SELECT	1
		from	pls_reajuste x
		where	x.dt_reajuste 		= a.dt_reajuste
		and (x.nr_seq_contrato 	= a.nr_seq_contrato or x.nr_seq_intercambio = a.nr_seq_intercambio)
		and	x.nr_sequencia 		< a.nr_sequencia
		and	exists (	select	1
					from	pls_reajuste z
					where	z.nr_sequencia 	= x.nr_seq_lote_deflator)
		and	exists (	select	1
					from	pls_rpc_contrato	y
					where	y.nr_seq_reajuste = x.nr_sequencia));

if (qt_reajuste_mes_w > 0) then
	ds_retorno_w := 'S';
else
	ds_retorno_w := 'N';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
