CREATE OR REPLACE FUNCTION pls_obter_se_regra_aut_vig ( cd_material_p material.cd_material%type) RETURNS varchar AS $body$
DECLARE

--verifica se existe alguma regra de autorização para o material vigente.
ie_regra_vigente_w	varchar(1);


BEGIN
-- inicia não tendo regra vigente
ie_regra_vigente_w := 'N';

-- se foi passado o material faz a consulta
if (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then

	select	coalesce(max(a.ie_liberado), 'N')
	into STRICT	ie_regra_vigente_w
	from	pls_regra_autorizacao a,
		pls_material b
	where	b.cd_material = cd_material_p
	and	a.nr_seq_material = b.nr_sequencia
	and	clock_timestamp() between a.dt_inicio_vigencia_ref and a.dt_fim_vigencia_ref
	and	a.ie_situacao = 'A';
end if;

return ie_regra_vigente_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
