CREATE OR REPLACE FUNCTION pls_obter_se_regra_bloqueio ( nr_seq_usuario_web_p bigint, nr_seq_local_atend_p bigint, nr_seq_maquina_p bigint, ds_endereco_mac_p text, ie_funcao_bloq_p bigint) RETURNS varchar AS $body$
DECLARE

/*
ie_funcao_bloq_p
1 - OPSW - Requisições para Autorização
2 - OPSW - Execução da Requisição para Autorização
*/
nr_seq_regra_bloq_w	bigint;
qt_registros_w		bigint;
ds_mensagem_w		varchar(4000);

C01 CURSOR FOR
	SELECT	nr_sequencia,
		coalesce(ds_mensagem,'Essa função está bloqueada!')
	from 	pls_regra_bloqueio_funcao
	where	coalesce(ie_funcao_bloqueio,ie_funcao_bloq_p) = ie_funcao_bloq_p
	and	ie_validar_mac_maquina = 'S'
	and	ie_situacao = 'A'
	and (cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento and (pls_obter_se_controle_estab('RE') = 'S'))
	
union all

	SELECT	nr_sequencia,
		coalesce(ds_mensagem,'Essa função está bloqueada!')
	from 	pls_regra_bloqueio_funcao
	where	coalesce(ie_funcao_bloqueio,ie_funcao_bloq_p) = ie_funcao_bloq_p
	and	ie_validar_mac_maquina = 'S'
	and	ie_situacao = 'A'
	and (pls_obter_se_controle_estab('RE') = 'N');

BEGIN

open C01;
loop
fetch C01 into
	nr_seq_regra_bloq_w,
	ds_mensagem_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
end loop;
close C01;

if (nr_seq_regra_bloq_w IS NOT NULL AND nr_seq_regra_bloq_w::text <> '') then

	/* A sequência da máquina já é validada quando o prestador loga no portal */

	if (nr_seq_maquina_p is  null) then
		select	count(1)
		into STRICT	qt_registros_w
		from	pls_excecao_bloq_funcao a
		where	((coalesce(a.nr_seq_usuario_web::text, '') = '')
		or (a.nr_seq_usuario_web	= nr_seq_usuario_web_p))
		and	((coalesce(a.nr_seq_local_atend::text, '') = '')
		or (a.nr_seq_local_atend	= nr_seq_local_atend_p))
		and	nr_seq_bloq_funcao	= nr_seq_regra_bloq_w;

		if (qt_registros_w	> 0) then
			ds_mensagem_w	:= 'N';
		end if;
	else
		ds_mensagem_w := 'N';
	end if;
else
	ds_mensagem_w := 'N';
end if;

return	ds_mensagem_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
