CREATE OR REPLACE FUNCTION pls_obter_se_regra_cbo (ie_tipo_guia_p pls_conta.ie_tipo_guia%type, nr_seq_prestador_p pls_conta.nr_seq_prestador_exec%type, nr_seq_tipo_atendimento_p pls_conta.nr_seq_tipo_atendimento%type) RETURNS varchar AS $body$
DECLARE


ie_grupo_prestador_w		varchar(1) := 'S';
nr_seq_regra_w			pls_regra_cbo.nr_sequencia%type;
ie_retorno_w			varchar(1)	:= 'N';
ie_ocorrencia_w			pls_controle_estab.ie_ocorrencia%type := 'N';
cd_estbalecimento_w		estabelecimento.cd_estabelecimento%type;

C01 CURSOR(	ie_ocorrencia_pc	pls_controle_estab.ie_ocorrencia%type,
		cd_estbalecimento_pc	estabelecimento.cd_estabelecimento%type) FOR
	SELECT	nr_seq_grupo_prestador,
		nr_sequencia,
		ie_opcao
	from	pls_regra_cbo
	where	((ie_tipo_guia = ie_tipo_guia_p) or (coalesce(ie_tipo_guia::text, '') = ''))
	and	((nr_seq_tipo_atendimento = nr_seq_tipo_atendimento_p) or (coalesce(nr_seq_tipo_atendimento::text, '') = ''))
	and	((nr_seq_prestador = nr_seq_prestador_p) or (coalesce(nr_seq_prestador::text, '') = ''))
	and	ie_ocorrencia_pc = 'N'
	
union all

	SELECT	nr_seq_grupo_prestador,
		nr_sequencia,
		ie_opcao
	from	pls_regra_cbo
	where	((ie_tipo_guia = ie_tipo_guia_p) or (coalesce(ie_tipo_guia::text, '') = ''))
	and	((nr_seq_tipo_atendimento = nr_seq_tipo_atendimento_p) or (coalesce(nr_seq_tipo_atendimento::text, '') = ''))
	and	((nr_seq_prestador = nr_seq_prestador_p) or (coalesce(nr_seq_prestador::text, '') = ''))
	and	ie_ocorrencia_pc = 'S'
	and	cd_estabelecimento = cd_estbalecimento_pc;
BEGIN

ie_ocorrencia_w := pls_obter_se_controle_estab('GO');
cd_estbalecimento_w := wheb_usuario_pck.get_cd_estabelecimento;

for C01_w in C01(ie_ocorrencia_w, cd_estbalecimento_w) loop
	begin
	ie_grupo_prestador_w := 'S';

	if (c01_w.nr_seq_grupo_prestador IS NOT NULL AND c01_w.nr_seq_grupo_prestador::text <> '') then
		ie_grupo_prestador_w := pls_obter_se_grupo_prestador(nr_seq_prestador_P, c01_w.nr_seq_grupo_prestador);
	end if;

	if (ie_grupo_prestador_w= 'S') and (c01_w.ie_opcao = 'GO')then
		nr_seq_regra_w := c01_w.nr_sequencia;
		exit;
	end if;
	end;
end loop;

if (nr_seq_regra_w IS NOT NULL AND nr_seq_regra_w::text <> '') then
	ie_retorno_w := 'S';
end if;

return	ie_retorno_w;

end;
