CREATE OR REPLACE FUNCTION pls_obter_se_regra_despesa_aud ( ie_tipo_despesa_p text, ie_tipo_item_p text, nr_seq_grupo_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_grupo_w			bigint;
ie_tipo_despesa_w		smallint;
ds_retorno_w			varchar(1)	:= 'N';
qt_registro_w			integer	:= 0;
ie_tipo_despesa_regra_w		smallint;


BEGIN

select	count(*)
into STRICT	qt_registro_w
from	pls_regra_grupo_despesa
where	nr_seq_grupo = nr_seq_grupo_p
and	ie_situacao = 'A';

if (qt_registro_w > 0) then
	if (ie_tipo_item_p = 'P') then
		if (ie_tipo_despesa_p = '1') then
			ie_tipo_despesa_regra_w := 3;
		elsif (ie_tipo_despesa_p = '2') then
			ie_tipo_despesa_regra_w := 4;
		elsif (ie_tipo_despesa_p = '3') then
			ie_tipo_despesa_regra_w := 5;
		end if;
	elsif (ie_tipo_item_p = 'M') then
		if (ie_tipo_despesa_p = '1') then
			ie_tipo_despesa_regra_w := 7;
		elsif (ie_tipo_despesa_p = '2') then
			ie_tipo_despesa_regra_w := 1;
		elsif (ie_tipo_despesa_p = '3') then
			ie_tipo_despesa_regra_w := 2;
		elsif (ie_tipo_despesa_p = '7') then
			ie_tipo_despesa_regra_w := 6;
		end if;
	end if;

	select	count(*)
	into STRICT	qt_registro_w
	from	pls_regra_grupo_despesa
	where	nr_seq_grupo = nr_seq_grupo_p
	and	ie_situacao = 'A'
	and	ie_tipo_despesa = ie_tipo_despesa_regra_w;

	if (qt_registro_w > 0) then
		ds_retorno_w := 'S';
	end if;
else
	ds_retorno_w := 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
