CREATE OR REPLACE FUNCTION pls_obter_se_regra_integ_aut ( nr_seq_prestador_p bigint, cd_motivo_glosa_p text, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


nr_seq_motivo_glosa_w		bigint;
ie_regra_integracao_ops_w	varchar(1);
ds_retorno_w			varchar(1) := 'N';
qt_reg_w			bigint;
ie_aplicacao_regra_w		varchar(2) := 'N';


BEGIN

select	coalesce(nr_sequencia,0),
	ie_regra_integracao_ops
into STRICT	nr_seq_motivo_glosa_w,
	ie_regra_integracao_ops_w
from	tiss_motivo_glosa
where	cd_motivo_tiss	= cd_motivo_glosa_p
and	coalesce(cd_convenio::text, '') = '';

if (ie_regra_integracao_ops_w = 'S') then

	if (nm_usuario_p <> 'WebService') then
		ie_aplicacao_regra_w := 'IT';
	end if;

	select	count(1)
	into STRICT	qt_reg_w
	from	pls_regra_glosa_oper
	where	nr_seq_motivo_glosa = nr_seq_motivo_glosa_w
	and	coalesce(nr_seq_prestador,nr_seq_prestador_p) = nr_seq_prestador_p
	and	ie_situacao = 'A'
	and	ie_aplicacao_regra = ie_aplicacao_regra_w;

	if (qt_reg_w > 0) then
		ds_retorno_w := 'S';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
