CREATE OR REPLACE FUNCTION pls_obter_se_regra_pergunt_faq ( nr_seq_pergunta_p bigint, nr_seq_tipo_ocorrencia_p bigint, nr_seq_evento_p bigint, nr_seq_grupo_atend_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(1) := 'S';
qt_reg_w			integer;
nr_seq_grupo_atend_w		bigint := 0;


BEGIN

select	count(*)
into STRICT	qt_reg_w
from	pls_pergunta_atend_evento
where	nr_seq_pergunta = nr_seq_pergunta_p;

if (coalesce(nr_seq_grupo_atend_p,0) > 0) then
	nr_seq_grupo_atend_w := nr_seq_grupo_atend_p;
end if;

if (qt_reg_w > 0) then

	select	count(*)
	into STRICT	qt_reg_w
	from	pls_pergunta_atend_evento
	where	nr_seq_pergunta = nr_seq_pergunta_p
	and	coalesce(nr_seq_tipo_ocorrencia,nr_seq_tipo_ocorrencia_p) = nr_seq_tipo_ocorrencia_p
	and	coalesce(nr_seq_evento,nr_seq_evento_p) = nr_seq_evento_p
	and	coalesce(nr_seq_grupo_atend,nr_seq_grupo_atend_w) = nr_seq_grupo_atend_w
	and	ie_situacao = 'A';

	if (qt_reg_w = 0) then
		ds_retorno_w := 'N';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
