CREATE OR REPLACE FUNCTION pls_obter_se_regra_senha_estip ( nr_seq_segurado_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w			varchar(2)	:= 'N';
nr_seq_contrato_w		bigint;
nr_seq_intercambio_w		bigint;

C01 CURSOR FOR
	SELECT	'S'
	from	pls_regra_senha_estip
	where	((coalesce(nr_seq_segurado::text, '') = '') 	or (nr_seq_segurado	= nr_seq_segurado_p))
	and	((coalesce(nr_seq_contrato::text, '') = '') 	or (nr_seq_contrato	= nr_seq_contrato_w))
	and	((coalesce(nr_seq_intercambio::text, '') = '')	or (nr_seq_intercambio	= nr_seq_intercambio_w))
	and	ie_situacao	= 'A'
	and (cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento and (pls_obter_se_controle_estab('RE') = 'S'))
	
union all

	SELECT	'S'
	from	pls_regra_senha_estip
	where	((coalesce(nr_seq_segurado::text, '') = '') 	or (nr_seq_segurado	= nr_seq_segurado_p))
	and	((coalesce(nr_seq_contrato::text, '') = '') 	or (nr_seq_contrato	= nr_seq_contrato_w))
	and	((coalesce(nr_seq_intercambio::text, '') = '')	or (nr_seq_intercambio	= nr_seq_intercambio_w))
	and	ie_situacao	= 'A'
	and (pls_obter_se_controle_estab('RE') = 'N');


BEGIN

begin
select	nr_seq_contrato,
	nr_seq_intercambio
into STRICT	nr_seq_contrato_w,
	nr_seq_intercambio_w
from	pls_segurado
where	nr_sequencia	= nr_seq_segurado_p;
exception
when others then
	nr_seq_contrato_w	:= null;
	nr_seq_intercambio_w	:= null;
end;

open C01;
loop
fetch C01 into
	ie_retorno_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
end loop;
close C01;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
