CREATE OR REPLACE FUNCTION pls_obter_se_regra_temp_exist ( qt_tempo bigint, nr_seq_regra_tempo_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(1);


BEGIN

if (pls_obter_se_controle_estab('GA') = 'S') then
	begin
		select	'S'
		into STRICT	ds_retorno_w
		from	pls_atend_regra_tempo
		where	qt_tempo_maximo = qt_tempo
		and	nr_seq_regra_tempo_p <> nr_sequencia
		and (cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento );
	exception
	when others then
		ds_retorno_w := 'N';
	end;
else
	begin
		select	'S'
		into STRICT	ds_retorno_w
		from	pls_atend_regra_tempo
		where	qt_tempo_maximo = qt_tempo
		and	nr_seq_regra_tempo_p <> nr_sequencia;
	exception
	when others then
		ds_retorno_w := 'N';
	end;
end if;

return	ds_retorno_w;

end;
