SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_se_req_vinculadas ( nr_seq_requisicao_p bigint) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Caso a requisição possua outras requisições vinculadas a ela, irá retornar 'S'.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros: Utilizada no campo function IE_POSSUI_REFERENCIA da tabela PLS_AUDITORIA
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção: Performance.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
/*
IE_RETORNO_W	= 'P' (Principal)
IE_RETORNO_W	= 'F' (Filha)
*/
ie_retorno_w			varchar(2)	:= 'N';
nr_seq_guia_w			bigint;
qt_registro_princ_w		bigint;
qt_registro_filha_w		bigint;


BEGIN

select	max(b.nr_sequencia)
into STRICT	nr_seq_guia_w
from    pls_guia_plano          b,
	pls_execucao_requisicao	a
where   b.nr_sequencia 		= a.nr_seq_guia
and     a.nr_seq_requisicao	= nr_seq_requisicao_p;

if (coalesce(nr_seq_guia_w,0)	<> 0) then
	select	count(1)
	into STRICT	qt_registro_princ_w
	from	pls_requisicao
	where	nr_seq_guia_principal	= nr_seq_guia_w;

	if (qt_registro_princ_w	= 0) then
		select	max(nr_seq_guia_principal)
		into STRICT	nr_seq_guia_w
		from	pls_requisicao
		where	nr_sequencia	= nr_seq_requisicao_p;

		if (coalesce(nr_seq_guia_w,0)	<> 0) then
			select	count(1)
			into STRICT	qt_registro_filha_w
			from    pls_guia_plano          b,
				pls_execucao_requisicao	a
			where   b.nr_sequencia 		= a.nr_seq_guia
			and     b.nr_sequencia		= nr_seq_guia_w;

		end if;
	end if;
else
	select	max(nr_seq_guia_principal)
	into STRICT	nr_seq_guia_w
	from    pls_requisicao
	where   nr_sequencia 	= nr_seq_requisicao_p;

	if (coalesce(nr_seq_guia_w,0)	<> 0) then
		select	count(1)
		into STRICT	qt_registro_filha_w
		from    pls_guia_plano          b,
			pls_execucao_requisicao	a
		where   b.nr_sequencia 		= a.nr_seq_guia
		and     b.nr_sequencia		= nr_seq_guia_w;

	end if;
end if;

if (qt_registro_princ_w	> 0) then
	ie_retorno_w	:= 'P';
elsif (qt_registro_filha_w	> 0) then
	ie_retorno_w	:= 'F';
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_se_req_vinculadas ( nr_seq_requisicao_p bigint) FROM PUBLIC;

