CREATE OR REPLACE FUNCTION pls_obter_se_rescisao_proporc ( nr_seq_contrato_p bigint, nr_seq_intercambio_p bigint, ie_tipo_contrato_p text, dt_mesano_referencia_p timestamp, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


--ie_tipo_contrato_p	PF, PJ
nr_seq_regra_resc_proporc_w	pls_regra_mens_contrato.nr_sequencia%type	:= 0;
ie_rescisao_proporcional_w	pls_regra_mens_contrato.ie_rescisao_proporcional%type;


BEGIN
if (coalesce(nr_seq_intercambio_p,0) <> 0) then
	select	max(a.nr_sequencia)
	into STRICT	nr_seq_regra_resc_proporc_w
	from	pls_regra_mens_contrato	a
	where	a.nr_seq_intercambio	= nr_seq_intercambio_p
	and	a.ie_tipo_regra		= 'G'
	and	trunc(dt_mesano_referencia_p,'dd') between a.dt_inicio_vigencia and coalesce(a.dt_fim_vigencia,dt_mesano_referencia_p);
else
	select	max(a.nr_sequencia)
	into STRICT	nr_seq_regra_resc_proporc_w
	from	pls_regra_mens_contrato	a
	where	a.nr_seq_contrato	= nr_seq_contrato_p
	and	a.ie_tipo_regra		= 'G'
	and	trunc(dt_mesano_referencia_p,'dd') between a.dt_inicio_vigencia and coalesce(a.dt_fim_vigencia,dt_mesano_referencia_p);
end if;

if (coalesce(nr_seq_regra_resc_proporc_w,0) = 0) then
	select	max(nr_sequencia)
	into STRICT	nr_seq_regra_resc_proporc_w
	from	pls_regra_mens_contrato
	where	coalesce(nr_seq_contrato::text, '') = ''
	and	coalesce(nr_seq_intercambio::text, '') = ''
	and	ie_tipo_regra		= 'G'
	and	cd_estabelecimento	= cd_estabelecimento_p
	and	trunc(dt_mesano_referencia_p,'dd') between dt_inicio_vigencia and coalesce(dt_fim_vigencia,dt_mesano_referencia_p)
	and	((coalesce(ie_pessoa_contrato,'A') = ie_tipo_contrato_p) or (ie_pessoa_contrato = 'A'));
end if;

if (coalesce(nr_seq_regra_resc_proporc_w::text, '') = '') then
	ie_rescisao_proporcional_w	:= 'N';
else
	select	coalesce(ie_rescisao_proporcional,'N')
	into STRICT	ie_rescisao_proporcional_w
	from	pls_regra_mens_contrato
	where	nr_sequencia	= nr_seq_regra_resc_proporc_w;
end if;

return	coalesce(ie_rescisao_proporcional_w,'N');

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
