CREATE OR REPLACE FUNCTION pls_obter_se_restric_financ ( nr_seq_segurado_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w            varchar(30);
qt_mens_aberta_w        bigint;


BEGIN

select  count(*)
into STRICT    qt_mens_aberta_w
from    pls_mensalidade_segurado        c,
        pls_mensalidade                 b,
        titulo_receber                  a
where   a.nr_seq_mensalidade 	= b.nr_sequencia
and     b.nr_sequencia 		= c.nr_seq_mensalidade
and     coalesce(a.dt_liquidacao::text, '') = ''
and	trunc(b.dt_vencimento,'dd') < trunc(clock_timestamp(),'dd')
and     c.nr_seq_segurado 	= nr_seq_segurado_p;

if (qt_mens_aberta_w > 0) then
        ds_retorno_w := 'Com restrição';
else
        ds_retorno_w := 'Sem restrição';
end if;

return  ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
