CREATE OR REPLACE FUNCTION pls_obter_se_rpc_enviado ( dt_referencia_p timestamp) RETURNS varchar AS $body$
DECLARE
	-- Change this to reflect the dblink connection string
	v_conn_str  text := format('port=%s dbname=%s user=%s', current_setting('port'), current_database(), current_user);
	v_query     text;

	v_ret	varchar;
BEGIN
	v_query := 'SELECT * FROM pls_obter_se_rpc_enviado_atx ( ' || quote_nullable(dt_referencia_p) || ' )';
	SELECT * INTO v_ret FROM dblink(v_conn_str, v_query) AS p (ret varchar);
	RETURN v_ret;

