CREATE OR REPLACE FUNCTION pls_obter_se_rpc_enviado_atx ( dt_referencia_p timestamp) RETURNS varchar AS $body$
DECLARE


qt_registro_w	integer;
ie_retorno_w	varchar(1);
BEGIN
ie_retorno_w	:= 'N';

select	count(1)
into STRICT	qt_registro_w
from	pls_rpc_lote
where	dt_referencia_p	between trunc(coalesce(dt_inicial,dt_referencia_p),'dd') and fim_dia(coalesce(dt_final,dt_referencia_p))
and	coalesce(nr_seq_lote_reajuste::text, '') = ''
and	(dt_envio IS NOT NULL AND dt_envio::text <> '');

if (qt_registro_w = 0) then
	select	count(1)
	into STRICT	qt_registro_w
	from	pls_rpc_lote	a,
		pls_reajuste	b
	where	b.nr_sequencia	= a.nr_seq_lote_reajuste
	and	trunc(b.dt_aplicacao_reajuste,'mm') = trunc(dt_referencia_p,'mm')
	and	(a.dt_envio IS NOT NULL AND a.dt_envio::text <> '');
end if;

if (qt_registro_w > 0) then
	ie_retorno_w	:= 'S';
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
