SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_se_sca_incl_contr ( nr_seq_benef_proposta_p bigint, nr_seq_plano_sca_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_inclusao_benef_w		bigint;
qt_registros_w			bigint;
ie_selecao_w			pls_inclusao_benef_sca.ie_selecao%type;


BEGIN

if (nr_seq_benef_proposta_p IS NOT NULL AND nr_seq_benef_proposta_p::text <> '') then
	select	nr_seq_inclusao_benef
	into STRICT	nr_seq_inclusao_benef_w
	from	pls_proposta_beneficiario
	where	nr_sequencia	= nr_seq_benef_proposta_p;
end if;

/*Se a proposta não for de inclusão*/

if (coalesce(nr_seq_inclusao_benef_w::text, '') = '') then
	return 'S';
end if;

select	count(1)
into STRICT	qt_registros_w
from	pls_inclusao_benef_sca
where	nr_seq_inclusao_benef	= nr_seq_inclusao_benef_w  LIMIT 1;

/*Se o usuário estipulante não utilizar nenhum parâmetro para controle de SCA*/

if (qt_registros_w = 0) then
	return 'S';
end if;

select	count(1)
into STRICT	qt_registros_w
from	pls_inclusao_benef_sca
where	nr_seq_inclusao_benef	= nr_seq_inclusao_benef_w
and	nr_seq_sca		= nr_seq_plano_sca_p  LIMIT 1;

/*Se o SCA não estiver na tabela de seleção*/

if (qt_registros_w = 0) then
	return 'S';
end if;

select	max(ie_selecao)
into STRICT	ie_selecao_w
from	pls_inclusao_benef_sca
where	nr_seq_inclusao_benef	= nr_seq_inclusao_benef_w
and	nr_seq_sca		= nr_seq_plano_sca_p;

return	ie_selecao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
