CREATE OR REPLACE FUNCTION pls_obter_se_scs_ativo () RETURNS varchar AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Obter se o SCS esta ativo na base do cliente
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[X]  Objetos do dicionário [X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:NÃo MEXER ANTES DE FALAR COM O DJAVAN
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ie_retorno_w	varchar(1);


BEGIN

begin
	select	CASE WHEN count(1)=1 THEN 'A'  ELSE 'I' END
	into STRICT	ie_retorno_w
	from	pls_end_webservice_scs
	where	ie_situacao = 'A';
exception
when others then
	ie_retorno_w	:= 'I';
end;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
