CREATE OR REPLACE FUNCTION pls_obter_se_seg_lote (nr_seq_segurado_p bigint, nr_seq_lote_p bigint, ie_tipo_lote_p text) RETURNS varchar AS $body$
DECLARE


/* IE_TIPO_LOTE_P
	V - Vencimento
	E - Emissao
*/
ds_retorno_w		varchar(1);
qt_seg_lote_w		smallint	:= 0;


BEGIN

ds_retorno_w := 'N';

select	count(*)
into STRICT	qt_seg_lote_w
from	pls_lote_carteira 	c,
	pls_carteira_emissao	b,
	pls_segurado_carteira	a
where	a.nr_sequencia		= b.nr_seq_seg_carteira
and	b.nr_seq_lote		= c.nr_sequencia
and	a.nr_seq_segurado	= nr_seq_segurado_p
and	c.nr_sequencia 		= nr_seq_lote_p
and	c.ie_tipo_lote 		= ie_tipo_lote_p;

if (qt_seg_lote_w > 0) then
	ds_retorno_w := 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
