SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_se_simultaneo_req ( nr_seq_simultaneo_p bigint, nr_seq_requisicao_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, nr_seq_material_p bigint, nr_seq_item_p bigint, ie_tipo_item_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  X]  Objetos do dicionário [ X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w			varchar(1) 	:= 'N';
qt_regra_w			integer	:= 1;
ie_regra_w			varchar(1)	:= 'N';
ie_item_sxclusivo_w		varchar(1)	:= 'N';
qt_proc_estrutura_w		integer;
qt_proc_estrutura_simul_w	integer;


BEGIN

begin
	select	coalesce(ie_item_exclusivo,'N'),
		'S'
	into STRICT	ie_item_sxclusivo_w,
		ie_regra_w
	from	pls_ocorrencia_simultaneo
	where	ie_situacao	= 'A'
	and	nr_sequencia	= nr_seq_simultaneo_p;
exception
when others then
	ie_item_sxclusivo_w := null;
end;

if (ie_regra_w = 'S') then

	if (cd_procedimento_p IS NOT NULL AND cd_procedimento_p::text <> '') then
		-- Essa verificação somente é realizada quando está sendo a consistência do procedimento
		select	count(1)
		into STRICT	qt_regra_w
		from	pls_ocorrencia_simul_item
		where	nr_seq_regra	= nr_seq_simultaneo_p
		and	cd_procedimento = cd_procedimento_p
		and	ie_origem_proced = ie_origem_proced_p
		and	ie_ocorrencia	= 'S'
		and	ie_situacao 	= 'A';
	end if;

	if (nr_seq_material_p IS NOT NULL AND nr_seq_material_p::text <> '') then
		-- Essa verificação somente é realizada quando está sendo a consistência do material
		select	count(1)
		into STRICT	qt_regra_w
		from	pls_ocorrencia_simul_item
		where	nr_seq_regra	= nr_seq_simultaneo_p
		and	nr_seq_material	= nr_seq_material_p
		and	ie_ocorrencia	= 'S'
		and	ie_situacao 	= 'A';
	end if;

	if (coalesce(qt_regra_w,0) > 0) then
		select	count(1)
		into STRICT	qt_proc_estrutura_w
		from	pls_ocorrencia_simul_item a
		where	a.nr_seq_regra = nr_seq_simultaneo_p
		and	a.ie_situacao	= 'A'
		and (exists (SELECT	b.nr_sequencia
				 from		pls_requisicao_proc b
				 where		b.nr_seq_requisicao = nr_seq_requisicao_p
				 and		b.cd_procedimento = a.cd_procedimento
				 and		b.ie_origem_proced = a.ie_origem_proced
				 group by	b.cd_procedimento,
						b.ie_origem_proced)
		or 	exists (select		c.nr_sequencia
				 from		pls_requisicao_mat c
				 where		c.nr_seq_requisicao = nr_seq_requisicao_p
				 and		c.nr_seq_material = a.nr_seq_material
				 group by	c.nr_seq_material));

		if (qt_proc_estrutura_w > 1) then
			if (ie_item_sxclusivo_w = 'S') then
				/*Obter quantos itens da estrutura so simultaneo existem*/

				select	count(1)
				into STRICT	qt_proc_estrutura_simul_w
				from	pls_ocorrencia_simul_item a
				where	a.nr_seq_regra = nr_seq_simultaneo_p
				and	((cd_procedimento	> 0) or (nr_seq_material	> 0))
				and	a.ie_situacao	= 'A';

				if (qt_proc_estrutura_w = qt_proc_estrutura_simul_w) then
					ds_retorno_w := 'S';
				end if;
			else
				ds_retorno_w := 'S';
			end if;
		end if;

	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_se_simultaneo_req ( nr_seq_simultaneo_p bigint, nr_seq_requisicao_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, nr_seq_material_p bigint, nr_seq_item_p bigint, ie_tipo_item_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) FROM PUBLIC;

