CREATE OR REPLACE FUNCTION pls_obter_se_solic_comercial ( nr_sequencia_p bigint, nr_telefone_p text, nr_celular_p text) RETURNS varchar AS $body$
DECLARE


qt_registro_w		bigint	:= 0;
ie_retorno_w		varchar(1)	:= 'N';


BEGIN

select	count(*)
into STRICT	qt_registro_w
from	pls_solicitacao_comercial
where	nr_sequencia <> nr_sequencia_p
and (nr_telefone = nr_telefone_p
	or nr_telefone = nr_celular_p
	or nr_celular = nr_telefone_p
	or nr_celular = nr_celular_p)
order by	1;

if (qt_registro_w > 0) then
	ie_retorno_w	:= 'S';
end if;

return	ie_retorno_w;

end;
