CREATE OR REPLACE FUNCTION pls_obter_se_tab_simul_perfil ( nr_seq_simulacao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(10);
nr_seq_simul_perfil_w		bigint;
nr_seq_tabela_w			bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_simulacao_perfil
	where	nr_seq_simulacao	= nr_seq_simulacao_p;


BEGIN

ds_retorno_w	:= 'S';

open C01;
loop
fetch C01 into
	nr_seq_simul_perfil_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	max(ie_tabela_selecionada)
	into STRICT	nr_seq_tabela_w
	from	PLS_SIMULACAO_PLANO
	where 	nr_seq_simul_perfil =  nr_seq_simul_perfil_w;

	if (coalesce(nr_seq_tabela_w::text, '') = '') then
		ds_retorno_w	:= 'N';
		exit;
	end if;

	end;
end loop;
close C01;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
