CREATE OR REPLACE FUNCTION pls_obter_se_titulo_cobran_pre ( nr_seq_cobranca_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w			varchar(2)	:= 'N';
qt_registros_w			bigint;


BEGIN

select	count(1)
into STRICT	qt_registros_w
from	titulo_receber
where	nr_seq_cob_previa	= nr_seq_cobranca_p;

if (qt_registros_w	> 0) then
	ie_retorno_w	:= 'S';
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
