CREATE OR REPLACE FUNCTION pls_obter_se_tit_camara (nr_titulo_pagar_p titulo_pagar.nr_titulo%type, nr_titulo_receber_p titulo_receber.nr_titulo%type) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255) 	:= 'N';
cont_w		bigint 	:= 0;


BEGIN

if (nr_titulo_pagar_p IS NOT NULL AND nr_titulo_pagar_p::text <> '') then
	select	count(1)
	into STRICT	cont_w
	from	pls_titulo_lote_camara
	where	nr_titulo_pagar = nr_titulo_pagar_p;
elsif (nr_titulo_receber_p IS NOT NULL AND nr_titulo_receber_p::text <> '') then
	select	count(1)
	into STRICT	cont_w
	from	pls_titulo_lote_camara
	where	nr_titulo_receber = nr_titulo_receber_p;
end if;

if (cont_w > 0) then
	ds_retorno_w := 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
