CREATE OR REPLACE FUNCTION pls_obter_se_tit_inadimplente ( nr_titulo_p bigint, dt_referencia_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);
dt_vencimento_w		timestamp;
dt_liquidacao_w		timestamp;


BEGIN

select	dt_pagamento_previsto,
	dt_liquidacao
into STRICT	dt_vencimento_w,
	dt_liquidacao_w
from	titulo_receber
where	nr_titulo	= nr_titulo_p;

if (dt_liquidacao_w IS NOT NULL AND dt_liquidacao_w::text <> '') then
	if (trunc(dt_liquidacao_w,'dd') > trunc(dt_vencimento_w,'dd')) then
		ds_retorno_w	:= 'S';
	else
		ds_retorno_w	:= 'N';
	end if;
else
	if (trunc(dt_vencimento_w,'dd') < trunc(dt_referencia_p,'dd')) then
		ds_retorno_w	:= 'S';
	else
		ds_retorno_w	:= 'N';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
