CREATE OR REPLACE FUNCTION pls_obter_se_tit_pag_escrit (nr_titulo_p bigint) RETURNS varchar AS $body$
DECLARE


cd_estabelecimento_w		integer;
ie_retorno_w			varchar(1)	:= 'S';
ie_exige_nf_pag_escrit_w	varchar(1);
nr_seq_nota_fiscal_w		bigint;
nr_seq_pls_pag_prest_w		varchar(10);


BEGIN

select	max(nr_seq_pls_pag_prest),
	max(nr_seq_nota_fiscal),
	max(cd_estabelecimento)
into STRICT	nr_seq_pls_pag_prest_w,
	nr_seq_nota_fiscal_w,
	cd_estabelecimento_w
from	titulo_pagar
where	nr_titulo	= nr_titulo_p;

if (nr_seq_pls_pag_prest_w IS NOT NULL AND nr_seq_pls_pag_prest_w::text <> '') then

	/* Verifica o par√¢metro */

	select	coalesce(max(ie_exige_nf_pag_escrit),'N')
	into STRICT	ie_exige_nf_pag_escrit_w
	from	pls_parametro_pagamento
	where	cd_estabelecimento	= cd_estabelecimento_w;

	if (ie_exige_nf_pag_escrit_w = 'S') then
		if (coalesce(nr_seq_nota_fiscal_w::text, '') = '') then
			ie_retorno_w	:= 'N';
		end if;
	end if;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
